                    dataType: 'json',                        url: "{{route('ajax.load_post')}}",                        data: {page: page},                        success: function(result)                        {                            page++;                            console.log('result', result);                            var html = '';                            $.each(result, function (index, value) {                                html += '<div class="col-md-4">' +                                    '<div class="post">' +                                    '<a class="post-img" href="#"><img src=' + value['thumbnail'] + '></a>' +                                    '<div class="post-body">' +                                    '<div class="post-meta">' +                                    '<a class="post-category cat-1" href="#"></a>' +                                    '<span class="post-date">March 27, 2018</span>' +                                    '</div>' +                                    '<h3 class="post-title"><a href="#">' + value['title'] + '</a></h3>' +                                    '</div>' +                                    '</div>' +                                    '</div>';                                if (index === 2) {                                    html += '<div class="clearfix visible-md visible-lg load-more"></div>';                                }                                $('.load-more').append(html)                            })                        },                    }).always(function () {                });            });        })    </script>@endsection