            var page = 1;            var limit = 4;            $('.btn_load_more').click(function () {                $.ajax(                    {                        type: 'get',                        dataType: 'json',                        url: "{{route('ajax.load_post')}}",                        data: {                            page: page,                            limit: limit                        },                        success: function(result)                        {                            if (result.length <= limit) {                                $('.btn_load_more').css('display', 'none');                            } else {                                result.splice(-1,1)                            }                            var html = '';                            $.each(result, function (index, value) {                                html += render_html(value);                            });                            $('.load_more_content').append(html);                            page++;                        },                    }).always(function () {                    });            });            function render_html(value) {                var html = '';                html +=                    '<div class="col-md-3">' +                    '<div class="post">' +                    '<a class="post-img" href="#"><img src=' + value['thumbnail'] + '></a>' +                    '<div class="post-body">' +                    '<div class="post-meta">' +                    '<a class="post-category cat-1" href="#"></a>' +                    '<span class="post-date">March 27, 2018</span>' +                    '</div>' +                    '<h3 class="post-title"><a href="#">' + value['title'] + '</a></h3>' +                    '</div>' +                    '</div>' +                    '</div>';                return html;            }        })    </script>@endsection